class SavedList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: []
    };
  }

  componentDidMount() {
    db.collection("peliculas")
      .get()
      .then(querySnapshot => {
        const data = querySnapshot.docs.map(doc => doc.data());
        console.log(data);
        this.setState({ data: data });
      });
  }

  render() {
    const { data } = this.state;
    return (
      <div className="row">
        {data.map(dat => (
                  <h5>{data.title}</h5>
        ))}
      </div>
    );
  }
}

export default SavedList;

